mapper.select:
SELECT temp.id,temp.total,temp.count,temp.sale_time,temp.person_name,cpbi.product_name,temp.product_id,temp.person_id FROM(SELECT id,total,count,sale_time,cbi.person_name person_name,product_id,cbi.person_id person_id FROM `product_sale_record` psr INNER JOIN `customer_basic_infomation` cbi ON psr.customer_id=cbi.person_id) temp INNER JOIN `common_product_basic_information` cpbi on cpbi.id=temp.product_id where 1=1
and cpbi.product_name like #{searchValue}"%" or temp.person_name like #{searchValue}"%"
and cpbi.product_name like "%"#{saleRecordMap.productName}"%"
and temp.person_name like "%"#{saleRecordMap.customerName}"%"
and temp.sale_time between #{timeRange[0]} and #{timeRange[1]}
limit #{pageNum},#{pageSize}
